// in app.js
import React from "react";
import {
  Admin,
  Resource,
  List,
  TextField,
  Datagrid,
  fetchUtils,
  Show,
  SimpleShowLayout,
  DateField,
  EditButton,
  RichTextField,
  ShowButton,
} from "react-admin";
import simpleRestProvider from "ra-data-simple-rest";

const dataProvider = simpleRestProvider("http://localhost:5000");

const App = () => (
  <Admin dataProvider={dataProvider}>
    <Resource name="posts" show={PostShow} list={PostList} />
  </Admin>
);

const PostList = props => (
  <List {...props}>
    <Datagrid>
      <TextField source="id" />
      <TextField source="title" />
      <TextField source="text" />
      <ShowButton />
    </Datagrid>
  </List>
);

const PostShow = props => (
  <Show {...props}>
    <SimpleShowLayout>
      <TextField source="title" />
      <TextField source="title" />
      <RichTextField source="text" />
    </SimpleShowLayout>
  </Show>
);

export default App;
